package JungOl;

import java.util.Scanner;

public class j515 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		sc.close();

		System.out.printf("%d * %d = %d\n", a, b, a * b);
		System.out.printf("%d / %d = %d", a, b, a / b);
	}

}
