package JungOl;

import java.util.Scanner;

public class j527 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		System.out.print(a / b + " ");
		System.out.printf("%.2f", (float) a / b);
		sc.close();
	}

}
